import './Hero.css';

export default function Hero({ selectedProject }) {
  const { description, goal, process, outcome, credits, technologies } = selectedProject;

  const companies = selectedProject.project_companies.company;
  const technologiesUsed = selectedProject.project_technologies.tools;

  return (
    <div className="hero-content">
      <div className="hero-title">
        <div className="companies">
          {
            companies.map((company) => (
              <div className="company">
                {company.name}
              </div>
            ))
          }
        </div>
        <div className="title">
          <h1>{selectedProject.project_title}</h1>
        </div>
        <div className="period">
          <h3>{selectedProject.project_period_begin}</h3>
          - 
          <h3>{selectedProject.project_period_end}</h3>
        </div>
        <div className="duration">
          <h4>{selectedProject.project_duration}</h4>
        </div>
      </div>
      <div className="container">
      {description && <p>{selectedProject.project_description}</p>}
      {goal && <p>{selectedProject.project_goal}</p>}
      {process && 
        <div className="phases">
          <div className="phase">
            {selectedProject.project_process.phase01}
          </div>
          <div className="phase">
            {selectedProject.project_process.phase02}
          </div>
          <div className="phase">
            {selectedProject.project_process.phase03}
          </div>
          <div className="phase">
            {selectedProject.project_process.phase04}
          </div>
        </div>
      }
      {outcome && <p>{selectedProject.project_outcome}</p>}
      {credits && 
        <div>
          <div>
            <h2>
              {Object.keys(selectedProject.project_credits.design)}
            </h2>
            <h3>
              {selectedProject.project_credits.design.lead_designer}
              {selectedProject.project_credits.design.designer}
            </h3>
          </div>
          <div>
            <h2>
              {Object.keys(selectedProject.project_credits.management)}
            </h2>
            <h3>
              {selectedProject.project_credits.management.counsel}
              {selectedProject.project_credits.management.project_manager}
            </h3>
          </div>
        </div>
      }
      {technologies && 
        <div>
          {
            technologiesUsed.map((tool) => (
              <div>
                {tool.name}
              </div>
            ))
          }
        </div>
      }
      </div>
    </div>
  );
}
